package presentation.utilimport kotlinx.datetime.Instantimport kotlinx.datetime.LocalDateTimeimport kotlinx.datetime.TimeZoneimport kotlinx.datetime.toLocalDateTimeobject DateTimeConverter {    fun toLocalDateTime(value: String?): LocalDateTime? {        return if (!value.isNullOrBlank()) {            try {                Instant.parse(value).toLocalDateTime(TimeZone.UTC)            } catch (e: Exception) {                null            }        } else null    }}/** * Converts a nullable ISO-8601 formatted string to a [LocalDateTime] (UTC timezone). * Returns `null` if: * - The input is `null`/blank, or * - Parsing fails (e.g., invalid format). * * Note: Consider logging parse failures in debug mode if invalid inputs are unexpected. * Example valid input: "2024-01-01T12:00:00Z". */